import React, { useEffect, useState } from "react";
import Button from "@mui/material/Button";
import axios from "axios";

const Payment = () => {
  const [user, setUser] = useState({});
  const [paymentId, setpaymentId] = useState("");
  const [orderId, setorderId] = useState("");
  const [signature, setsignature] = useState("");
  useEffect(() => {
    setUser(JSON.parse(localStorage.getItem("user_details")));
    console.log(user);
  }, []);
  const makePayment = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://localhost:5000/createorder");
    console.log(res.data);
    if (res.status !== 200) {
      return;
    }
    const options = {
      key: "rzp_test_Qey3iC23cu7rAo",
      amount: res.data.amount,
      currency: res.data.currency,
      name: "DebCart",
      description: res.data.notes.title,
      image: res.data.notes.image,
      order_id: res.data.id,
      handler: function (response) {
        setorderId(response.razorpay_order_id);
        setpaymentId(response.razorpay_payment_id);
        setsignature(response.razorpay_signature);
      },
      prefill: {
        name: user.name,
        email: user.email,
        contact: user.phone,
      },
      notes: {
        address: "Razorpay Corporate Office",
      },
      theme: {
        color: "#3399cc",
      },
    };
    var rzp1 = new Razorpay(options);
    rzp1.on("payment.failed", function (response) {
      alert(response.error.code);
      alert(response.error.description);
      alert(response.error.source);
      alert(response.error.step);
      alert(response.error.reason);
      alert(response.error.metadata.order_id);
      alert(response.error.metadata.payment_id);
    });
    document.getElementById("rzp-button1").onclick = function (e) {
      rzp1.open();
      e.preventDefault();
    };
  };
  return (
    <div className="payment">
      <Button variant="contained" onClick={(e) => makePayment(e)}>
        Make payment
      </Button>
    </div>
  );
};

export default Payment;
