import React, { useEffect, useState } from "react";
import "./styles/admin.css";
import "antd/dist/antd.css";
import { Spin } from "antd";
import axios from "axios";

const Admin = () => {
  const [loading, setLoading] = useState(false);
  const [product, setProduct] = useState({});
  const [user, setUser] = useState({});
  const [payid, setPayid] = useState("");
  const [orderId, setOrderId] = useState("");
  useEffect(() => {
    setLoading(true);
    axios
      .get("http://localhost:5000/product", {
        Accept: "application/json",
      })
      .then((res) => {
          setLoading(false);
        console.log(res.data);
        setProduct(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
    //   setUser(localStorage.getItem(JSON.parse("user_details")));
      setUser(JSON.parse(localStorage.getItem("user_details")));
      setOrderId(JSON.parse(localStorage.getItem("orderid")));
      setPayid(JSON.parse(localStorage.getItem("paymentId")));
      console.log(user);
  },[]);
//   useEffect(() => {
//     setUser(localStorage.getItem(JSON.parse("user_details")));
//     // setOrderId(localStorage.getItem(JSON.parse("orderid")));
//     // setPayid(localStorage.getItem(JSON.parse("paymentId")));
//   },[]);
  if (loading) {
    return (
      <div
        style={{
          margin: 0,
          padding: 0,
          height: "100vh",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          background: "#ececec",
        }}
      >
        <Spin tip="Loading...."></Spin>
      </div>
    );
  }
  return (
    <div className="admin_panel">
      <h2>hello world</h2>
      <div className="product">
        <img src={product.image} alt="Image" />
        <div className="order_details">
          <p className="title">
            <strong>{product.title}</strong>
          </p>
          <p className="price">
            <strong>{product.price}</strong>
          </p>
          <p className="order_id">
            <strong>{orderId}</strong>
          </p>
          <p className="payment_id">
            <strong>{payid}</strong>
          </p>
        </div>
      </div>
      {/* <div className="user">
        <p className="user_name">
          Order By : <strong>{user.name}</strong>
        </p>
        <p className="user_email">
          Email : <strong>{user.email}</strong>
        </p>
        <p className="user_phone">
          Phone : <strong>{user.phone}</strong>
        </p>
      </div> */}
    </div>
  );
};

export default Admin;
